import React from "react"
import { useQuery, gql } from "@apollo/client";
const QUERY_ALL_USERS = gql `
query GetAllUsers{
  users {
    age
    id
    name
    username
    nationality
  }
}
`;

const QUERY_ALL_MOVIES = gql `
query GetMovie{
  movies{
    name
    id
    yearOfPublication
    isInTheaters

  }
}

`;

function DisplayData (){
    const {data, loading, error} = useQuery(QUERY_ALL_USERS);
    const {data_movies} = useQuery(QUERY_ALL_MOVIES)
    if (loading){
        return <h1>Data is loading</h1>
    }
    if (data){
        console.log(data)
    }

    if(error){
        console.log(error)
    }
    return <div>{data_movies && data_movies.users.map((mov)=>{
        return <div>
            <h1>Name : {mov.name}</h1>
            <h1>ID : {mov.id}</h1>
            <h1>Year : {mov.yearOfPublication}</h1>
            <h1>Avaliable in Theatres: {mov.isInTheaters}</h1>
        </div>
        })}</div>
